mapper.select:
select ano,atitle,a.pno,l.lname,mnick,acontent
from askboard a ,problem p ,license l, member m, licenseclass lc
where a.pno=p.pno and lc.lqno=p.lqno and lc.lno=l.lno and a.mno=m.mno and a.ano=#{ano}

mapper.update:
update askboard
set atitle=#{atitle},
	acontent=#{acontent},
	pno=#{pno}
where ano=#{ano}

update askboard
set ahit=ahit+1
where ano=#{ano}
