select.if:
WHERE m.mno=#{mno}
ORDER BY p.pno desc
limit #{start}, #{size}


WHERE i.mno=#{mno}
ORDER BY p.pno desc
limit #{start}, #{size}

WHERE m.mno=#{mno} and p.pno =#{scontent}
ORDER BY p.pno desc
limit #{start}, #{size}


WHERE i.mno=#{mno} and p.pno =#{scontent}
ORDER BY p.pno desc
limit #{start}, #{size}

WHERE m.mno=#{mno} and pdetail like CONCAT('%',#{scontent},'%') 
ORDER BY p.pno desc
limit #{start}, #{size}


WHERE i.mno=#{mno} and pdetail like CONCAT('%',#{scontent},'%') 
ORDER BY p.pno desc
limit #{start}, #{size}

WHERE m.mno=#{mno} and p.lqno=#{selectKind}
ORDER BY p.pno desc
limit #{start}, #{size}


WHERE i.mno=#{mno} and p.lqno=#{selectKind}
ORDER BY p.pno desc
limit #{start}, #{size}

WHERE m.mno=#{mno} and p.lqno=#{selectKind} and pdetail like CONCAT('%',#{scontent},'%') 
ORDER BY p.pno desc
limit #{start}, #{size}


WHERE i.mno=#{mno} and p.lqno=#{selectKind} and pdetail like CONCAT('%',#{scontent},'%') 
ORDER BY p.pno desc
limit #{start}, #{size}

WHERE m.mno=#{mno} and p.lqno=#{selectKind} and p.pno=#{scontent}
ORDER BY p.pno desc
limit #{start}, #{size}


WHERE i.mno=#{mno} and p.lqno=#{selectKind} and p.pno=#{scontent}
ORDER BY p.pno desc
limit #{start}, #{size}

mapper.select:
SELECT p.pno,lc.lqno FROM problem AS p join licenseclass AS lc ON p.lqno=lc.lqno join member AS m ON m.mno=p.mno WHERE lc.lqno=#{lqno} AND (p.pyear=0
or pyear=#{item}
)
and m.mnick='관리자'
and m.mnick!='관리자'
